            <h2 class="text-lg font-semibold text-gray-700 capitalize">Change Account Settings</h2>

            <?php
                if (isset($_POST['update_settings'])) {
                    
                    $fullname = $_POST['fullname'] ?? '';
                    $current_password = $_POST['current_password'] ?? '';
                    $new_password = $_POST['new_password'] ?? '';
                    $repeat_password = $_POST['repeat_password'] ?? '';
                    $userId = $_SESSION['user_id'];

                    $sql = "SELECT * FROM `users` WHERE `id` = ?";
                    $stmt = $pdo->prepare($sql);
                    $stmt->execute([$userId]);
                    $user = $stmt->fetch(PDO::FETCH_ASSOC);

                    if ($user) {
                        if (!password_verify($current_password, $user['password'])) {
                            echo "<p style='color: red;'>Error: Current password is incorrect.</p>";
                        } elseif ($new_password !== $repeat_password) {
                            echo "<p style='color: red;'>Error: New password and repeat password do not match.</p>";
                        } else {
                            $hashedPassword = password_hash($new_password, PASSWORD_BCRYPT);
                            $sql = "UPDATE `users` SET `fullname` = ?, `password` = ? WHERE `id` = ?";
                            $stmt = $pdo->prepare($sql);

                            if ($stmt->execute([$fullname, $hashedPassword, $userId])) {
                                echo "<p style='color: green;'>Settings updated successfully!</p>";
                            } else {
                                echo "<p style='color: red;'>Error: Something went wrong during updating settings.</p>";
                            }
                        }
                    } else {
                        echo "<p style='color: red;'>Error: User not found.</p>";
                    }
                }
            ?>

                
